import {
    animate,
    state,
    style,
    transition,
    trigger
} from '@angular/animations';
import { Component, Input } from '@angular/core';
import { Movie } from '../../models/movie';

@Component({
    selector: 'app-slider',
    templateUrl: './slider.component.html',
    styleUrls: ['./slider.component.scss'],
    animations: [
        trigger('slideFade', [
            state('void ', style({ opacity: 0 })),
            transition('void => *', [animate('4s')])
        ])
    ]
})
export class SliderComponent {
    @Input() items: Movie[] = [];
}
<!-- <pre>{{ items | json }}</pre> -->

<div class="slider">
    <ng-container>
        <div class="slide" *ngFor="let item of items; let i = index" @slideFade>
            <div class="slide-bg">
                <img
                    class="slide-bg-image"
                    src="https://image.tmdb.org/t/p/w500/hZkgoQYus5vegHoetLkCJzb17zJ.jpg"
                    alt=""
                />
            </div>
            <div class="slide-pane">
                <h2 class="name">{{ item.title }} - {{ i }}</h2>
                <div class="meta">
                    <div class="info">
                        <span class="rating">
                            <span class="app-icon">star</span
                            ><span class="rating-value">{{
                                item.vote_average
                            }}</span
                            ><span class="separate">/10</span
                            ><span class="separate">{{ item.vote_count }}s</span
                            ><span>{{ item.release_date }}</span>
                        </span>
                    </div>
                </div>
                <div class="desc mt-6">
                    {{ item.overview }}
                </div>
            </div>
        </div>
    </ng-container>
</div>
