import { animate, state, style, transition, trigger } from '@angular/animations';
import { Component, Input, OnInit } from '@angular/core';
import { Movie } from '../../models/movie';

@Component({
    selector: 'app-slider',
    templateUrl: './slider.component.html',
    styleUrls: ['./slider.component.scss'],

    animations: [
        trigger('slideFade', [
            state('void', style({ opacity: 0 })),
            transition('void <=> *', [animate('1s')])
        ])
    ]
})
export class SliderComponent implements OnInit {
    @Input() items: Movie[] = [];

    currentSlideIndex: number = 0;

    ngOnInit(): void {
        setInterval(() => {
            this.currentSlideIndex = ++this.currentSlideIndex % this.items.length;
        }, 5000);
    }
}
<!-- <pre>{{ items | json }}</pre> -->

<div class="slider">
    <ng-container *ngFor="let item of items; let i = index">
        <div class="slide" *ngIf="i == currentSlideIndex" @slideFade>
            <div class="slide-bg">
                <img
                    class="slide-bg-image"
                    [src]="'https://image.tmdb.org/t/p/original/' + item.backdrop_path"
                    alt=""
                />
            </div>
            <div class="slide-pane">
                <h2 class="name">{{ item.title }}</h2>
                <div class="meta">
                    <div class="info">
                        <span class="rating">
                            <span class="app-icon">star</span
                            ><span class="rating-value">{{ item.vote_average }}</span
                            ><span class="separate">/10</span
                            ><span class="separate">{{ item.vote_count }}</span
                            ><span>{{ item.release_date | date : 'yyyy' }}</span>
                        </span>
                    </div>
                    <div class="desc mt-6">
                        {{ item.overview }}
                    </div>
                </div>
            </div>
        </div>
    </ng-container>
</div>
